mapper.select:
SELECT 

FROM brf_device a

WHERE a.seq = #{id}

SELECT
FROM brf_device a
AND a.seq LIKE '%'||#{seq}||'%' '%'+#{seq}+'%' concat('%',#{seq},'%')
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.aisle = #{aisle}
ORDER BY ${page.orderBy}

SELECT 

FROM brf_device a






		ORDER BY ${page.orderBy}

mapper.update:
UPDATE brf_device SET 	
	seq = #{seq},
	name = #{name},
	aisle = #{aisle},
	ver = #{ver}
WHERE id = #{id}
